} from '@angular/core';
import { DynamicCompDirective } from '../dynamic-comp.directive';
import { PopoverOptions } from '../popover.interface'
import {ActionAvatarComponent} from "src/stories/actions/action-avatar/action-avatar.component";


@Component({
